import numpy as npimport matplotlib.pylab as pltfrom ch03_module import *n = np.arange(-2,9)N = len(n)xn = np.zeros(N); xn[2]=1; xn[3]=0; xn[4]=-1; xn[5]=0; xn[6]=1; xn[7]=0; xn[8]=-1hn = np.zeros(N); hn[2]=1; hn[3]=1; hn[4]=1n1,y1 = convolve_m(n,xn,n,hn)n2,y2 = sigshift(n,xn,1)n3,y3 = sigshift(n,xn,2)plt.subplot(4,1,1); plt.stem(n, xn,"blue"); plt.grid(); plt.xlim(-1,9); plt.ylim(-2,2); plt.ylabel("step1")plt.subplot(4,1,2); plt.stem(n2, y2,"blue"); plt.grid(); plt.xlim(-1,9); plt.ylim(-2,2); plt.ylabel("step2")plt.subplot(4,1,3); plt.stem(n3, y3,"blue"); plt.grid(); plt.xlim(-1,9); plt.ylim(-2,2); plt.ylabel("step3")plt.subplot(4,1,4); plt.stem(n1, y1,"blue"); plt.grid(); plt.xlim(-1,9); plt.ylim(-2,2); plt.ylabel("result")